import React from 'react';

const apiBaseUrl = "https://jamb.azurewebsites.net";

data.username = prompt("Enter your username");
if (!data.username) {
  alert("No username entered. Reload page and try again.");
  throw "No username entered";
}

const connection = new signalR.HubConnectionBuilder()
  .withUrl(`${apiBaseUrl}/api`)
  .configureLogging(signalR.LogLevel.Information)
  .build();

connection.on('newMessage', newMessage);
connection.onclose(() => console.log('disconnected'));

console.log('connecting...');
connection.start()
  .then(() => data.ready = true)
  .catch(console.error);

function sendMessage(sender, messageText) {
  return axios.post(`${apiBaseUrl}/api/messages`, {
    sender: sender,
    text: messageText
  }).then(resp => resp.data);
}

let counter = 0;
function newMessage(message) {
  message.id = counter++; // vue transitions need an id
  data.messages.unshift(message);
}

const Index = () => (
  <div>chat</div>
);

export default Index;
