import React, { useEffect, useState } from 'react';
import axios from 'axios';

function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const data = {
  userName: "guest-" + new Date().uuidv4(),
};

const apiBaseUrl = "https://jamb.azurewebsites.net";

const Index = () => {
  const [isReady, setIsReady] = useState(false);
  const [messages, setMessages] = useState(new Array());
  const [newMessageText, setNewMessageText] = useState('');

  const sendMessageClick = () => {
    sendMessage(data.userName, newMessageText);
  };

  function sendMessage(sender, messageText) {
    return axios.post(`${apiBaseUrl}/api/messages`, {
      sender: sender,
      text: messageText
    }).then(resp => resp.data);
  }

  let counter = 0;
  function newMessage(message) {
    message.id = counter++;
    messages.unshift(message);
    setMessages([...messages]);
  }

  useEffect(() => {
    const connection = new window.signalR.HubConnectionBuilder()
      .withUrl(`${apiBaseUrl}/api`)
      .configureLogging(window.signalR.LogLevel.Information)
      .build();

    connection.on('newMessage', newMessage);
    connection.onclose(() => console.log('disconnected'));

    connection.start()
      .then(() => setIsReady(true))
      .catch((err) => {console.error(err); setIsReady(false);});
  }, []);

  return (
    <div>
      <h1>JAMB</h1>
      <div>{isReady ? "Connected" : "Loading..."}</div>
      <div>
        <input value={newMessageText} onChange={(e) => setNewMessageText(e.target.value)} />
        <button onClick={() => sendMessageClick()}>Salji</button>
      </div>
      <div>
        {messages.map((message) => (
          <div key={message.id}>({message.sender}) {message.text}</div>
        ))}
      </div>
    </div>
  );
};

export default Index;
